import UIKit

class ResultWorker {
    
    var dataService: DataService
    var apiKey: String
    
    init(dataService: DataService, apiKey: String) {
        self.dataService = dataService
        self.apiKey = apiKey
    }
    
    func requestSentimentAnalysis(text: String, completion: @escaping (AnalyzedSentiment?, NSError?) -> ()) {
        dataService.requestSentimentAnalysis(text: text) { (analyzeSentiment, error) in
            completion(analyzeSentiment, error)
        }
    }
}
